UN2008e0F <- read.delim(file='UN2008e0F.txt', comment.char='#')
# change the column names from the ones generated by R (X2000.2005) to the original ones (2000-2005)
get.new.e0.colnames <- function() {
        ncols <- ncol(UN2008e0F)
        colnames.years <- colnames(UN2008e0F)[4:ncols]
        colnames(UN2008e0F)[4:ncols] <- substr(colnames.years, 2, 10)
        substr(colnames(UN2008e0F)[4:ncols], 5,5) <- '-'
        return(colnames(UN2008e0F))
}
colnames(UN2008e0F) <- get.new.e0.colnames()
